import './career.scss';
import Header from '../header/header';
import { useState, useEffect, useRef } from 'react';

const Career = (): JSX.Element => {

    const refText = useRef<HTMLDivElement>(null);

    const resetHeight = () => {
        document.getElementById("test")!.style.minHeight = window.innerHeight + "px";
        document.getElementById("test")!.style.height = window.innerHeight + "px";
    }

    useEffect(() => {
        document.getElementById("test")!.style.minHeight = window.innerHeight + "px";
        document.getElementById("test")!.style.height = window.innerHeight + "px";
        window.addEventListener('resize', resetHeight);
        return () => window.removeEventListener('resize', resetHeight);

    }, [resetHeight]);

    return (
        <>
            <div className="career">
                <div id='test' className="career_wrapper" style={{ background: "red" }}>
                    <div className="career_wrapper__check"></div>
                </div>
                <div>Test</div>
            </div>
        </>
    );
}

export default Career;
