import './servicePage.scss';
import { useState, useEffect, useCallback, useRef } from 'react';
import Header from '../header/header';
import WebService from '../webService/webService';
import AppService from '../appService/appService';
import CloudService from '../cloudService/cloudService';
import AiService from '../aiService/aiService';
import EnterpriseService from '../enterpriseService/enterpriseService';


const ServicePage = (): JSX.Element => {

    const ref1 = useRef<HTMLDivElement>(null);
    const ref2 = useRef<HTMLDivElement>(null);
    const ref3 = useRef<HTMLDivElement>(null);
    const ref4 = useRef<HTMLDivElement>(null);
    const ref5 = useRef<HTMLDivElement>(null);


    const [serviceActive, setServiceActive] = useState<number>(1);

    const scrollHandler = useCallback(() => {
        if (ref1 && ref1.current && window.pageYOffset + window.innerHeight -150 >= ref1.current.offsetTop) {
            setServiceActive(1);
        }
        if (ref2 && ref2.current && window.pageYOffset + window.innerHeight -150 >= ref2.current.offsetTop) {
            setServiceActive(2);
        }
        if (ref3 && ref3.current && window.pageYOffset + window.innerHeight -150 >= ref3.current.offsetTop) {
            setServiceActive(3);
        }
        if (ref4 && ref4.current && window.pageYOffset + window.innerHeight -150 >= ref4.current.offsetTop) {
            setServiceActive(4);
        }
        if (ref5 && ref5.current && window.pageYOffset + window.innerHeight -150 >= ref5.current.offsetTop) {
            setServiceActive(5);
        }

    }, []);

    useEffect(() => {

        window.addEventListener('scroll', scrollHandler);
        return () => window.removeEventListener('scroll', scrollHandler);

    }, [scrollHandler]);

    return (
        <>
            <Header />
            <div className={`servicePage ${serviceActive === 2 ? "servicePage--dark" : ""}`}>
                <div className="servicePage__tiles">
                    <div className={`servicePage__tiles__wrapper ${serviceActive === 3 ? "servicePage__tiles__wrapper--scrollUp" : ""} ${serviceActive >= 4 ? "servicePage__tiles__wrapper--scrollUpUp" : ""}`}>
                        <div className={`servicePage__tiles__wrapper__tile ${serviceActive === 1 ? "servicePage__tiles__wrapper__tile--active" : ""} ${serviceActive === 2 ? "servicePage__tiles__wrapper__tile--dark" : ""}`}><span>Web</span></div>
                        <div className={`servicePage__tiles__wrapper__tile ${serviceActive === 2 ? "servicePage__tiles__wrapper__tile--activeDark" : ""}`}><span>App</span></div>
                        <div className={`servicePage__tiles__wrapper__tile ${serviceActive === 3 ? "servicePage__tiles__wrapper__tile--active" : ""} ${serviceActive === 2 ? "servicePage__tiles__wrapper__tile--dark" : ""}`}><span>Cloud</span></div>
                        <div className={`servicePage__tiles__wrapper__tile ${serviceActive === 4 ? "servicePage__tiles__wrapper__tile--active" : ""}`}><span>AI</span></div>
                        <div className={`servicePage__tiles__wrapper__tile ${serviceActive === 5 ? "servicePage__tiles__wrapper__tile--active" : ""}`}><span>Unknown</span></div>
                    </div>
                </div>
                <div className="servicePage__text">
                    <div ref={ref1} className="servicePage__text__wrapper">
                        <WebService />
                    </div>
                    <div ref={ref2} className="servicePage__text__wrapper">
                        <AppService />
                    </div>
                    <div ref={ref3} className="servicePage__text__wrapper">
                        <CloudService />
                    </div>
                    <div ref={ref4} className="servicePage__text__wrapper">
                        <AiService />
                    </div>
                    <div ref={ref5} className="servicePage__text__wrapper">
                        <EnterpriseService />
                    </div>
                </div>
            </div>
        </>
    );
}

export default ServicePage;
