import './serviceItem.scss';
import { useState, useEffect, useRef } from 'react';

interface ServiceItemProps {
    clasNameAdded: string;
    title: string;
    rowElements: JSX.Element;
}

const ServiceItem = ({ clasNameAdded = '', title = '', rowElements = <></> }: ServiceItemProps): JSX.Element => {

    const refItem = useRef<HTMLDivElement>(null);

    const [showService, setShowService] = useState<boolean>(false);

    useEffect(() => {

        window.addEventListener('scroll', scrollHandler);

        return () => window.removeEventListener('scroll', scrollHandler);

    }, []);

    const scrollHandler = () => {
        if (refItem 
                && refItem.current 
                && (window.pageYOffset + (window.innerHeight/2) >= refItem.current.offsetTop 
                && window.pageYOffset + (window.innerHeight/2) <= refItem.current.offsetTop + refItem.current.scrollHeight ) ) {
            setShowService(true);
        } else {
            setShowService(false);
        }
    }

    return (
        <div ref={refItem} className={`serviceItem ${showService ? "serviceItem--active" : ""} ${clasNameAdded}`}>
            {/* <div className="serviceItem__rowWrapper">{rowElements}</div> */}
            <div className="serviceItem__title"><span>{title}</span></div>
        </div>
    );
}

export default ServiceItem;
