import './landing.scss';
import { useState, useEffect, useRef } from 'react';
import Header from '../header/header';
import Footer from '../footer/footer';
import PhoneRight from '../phoneRight/phoneRight';
import { Link } from 'react-router-dom';
import Intro from '../intro/intro';
import FadeIn from '../fadeIn/fadeIn';

const Landing = (): JSX.Element => {

    const refMiddle = useRef<HTMLDivElement>(null);
    const refEnding = useRef<HTMLDivElement>(null);


    const [activeBegin, setActiveBegin] = useState<boolean>(false);
    const [activeMiddle, setActiveMiddle] = useState<boolean>(false);
    const [intro] = useState<boolean>(!sessionStorage.getItem("intro") ? true : false);


    const messageScreen: JSX.Element = <div className={`landing__middle__phoneWrapper__messageWrapper ${activeMiddle ? "landing__middle__phoneWrapper__messageWrapper--active" : ""}`}><span>Help?</span></div>;

    return (
        <>
            {intro && <Intro />}
            <Header dark={false} />
            <div className="landing">
                <FadeIn setPropActive={setActiveBegin}>
                    <div className="landing__begin">
                        <svg xmlns="http://www.w3.org/2000/svg" height="100%" width="100%" viewBox="0 0 500 246"
                            preserveAspectRatio="xMinYMid meet">
                            <defs>
                                <pattern id="titlePattern" x="0" y="0" patternUnits="userSpaceOnUse" width="500" height="246"
                                    viewBox="0 0 500 246">
                                    <text x="0" y="71">Design</text>
                                    <text x="0" y="153">Build</text>
                                    <text x="0" y="235">Maintain</text>
                                </pattern>
                            </defs>
                            <g className="title" fill="url(#titlePattern)">
                                <rect className={`${activeBegin ? "title__odd--explode" : "title__odd"}`} x="0" y="0" width="500" height="42" />
                                <rect x="0" y="41" width="500" height="42" />
                                <rect className={`${activeBegin ? "title__odd--explode" : "title__odd"}`} x="0" y="81" width="500" height="42" />
                                <rect x="0" y="122" width="500" height="42" />
                                <rect className={`${activeBegin ? "title__odd--explode" : "title__odd"}`} x="0" y="163" width="500" height="42" />
                                <rect x="0" y="204" width="500" height="42" />
                            </g>
                        </svg>
                    </div>
                </FadeIn>
                <div className="landing__border"></div>
                <FadeIn setPropActive={setActiveMiddle}>
                    <div ref={refMiddle} className="landing__middle">
                        <h1 className="landing__middle__title">We are a digital product studio based in the Netherlands</h1>
                        <PhoneRight classNameAdded={`phoneRight--red ${activeMiddle ? "phoneRight--active" : ""}`} messageScreen={messageScreen} explode={!activeMiddle} />
                    </div>
                </FadeIn>
                <div className="landing__border"></div>
                <FadeIn setPropActive={() => { }}>
                    <div ref={refEnding} className="landing__ending">
                        <Link to="/services">
                            <span>Services</span>
                            <span></span>
                        </Link>
                    </div>
                </FadeIn>
            </div>
            <Footer />
        </>
    );
}

export default Landing;
