import React, { useState, useEffect } from 'react';
import './labelTextarea.scss';

interface LabelTextareaProps {
    classNameAdded: string;
    placeHolder: string;
}

function LabelTextarea({classNameAdded = '', placeHolder =''}: LabelTextareaProps) {

    const [height, setHeight] = useState(60);
    const [scroll, setScroll] = useState(0);
    const [skip, setSkip] = useState(true);


    const onChange = (event: React.ChangeEvent<HTMLTextAreaElement>): void => {

        const scrollHeight = event.target.scrollHeight; 

        if(scrollHeight > scroll && !skip) {
            setHeight(height + 100);
            setSkip(true);
        } else {
            setSkip(false);
        }


        setScroll(scrollHeight);

    }

    return (
        <>
            <label className={`${classNameAdded} labelTextarea`}>
                <textarea style={{height: `${height }px`}}  className="labelTextarea__input" placeholder={placeHolder} onChange={onChange} />
                <span className="labelTextarea__placeholder">{placeHolder}</span>
            </label>
        </>
    );
}

export default LabelTextarea;
