import './servicePage.scss';
import { useState, useEffect, useCallback, useRef } from 'react';
import Header from '../header/header';
import ServiceItem from '../serviceItem/serviceItem';
import { Link } from 'react-router-dom';
import FadeIn from '../fadeIn/fadeIn';


const ServicePage = (): JSX.Element => {

    const ref1 = useRef<HTMLDivElement>(null);
    const ref2 = useRef<HTMLDivElement>(null);
    const ref3 = useRef<HTMLDivElement>(null);
    const ref4 = useRef<HTMLDivElement>(null);
    const ref5 = useRef<HTMLDivElement>(null);

    const [serv1, setServ1] = useState<boolean>(false);
    const [serv2, setServ2] = useState<boolean>(false);
    const [serv3, setServ3] = useState<boolean>(false);
    const [serv4, setServ4] = useState<boolean>(false);
    const [serv5, setServ5] = useState<boolean>(false);

    return (
        <>
            <Header dark={false} />
            <div className="servicePage">
                <div className="servicePage__tiles">
                    <div className={`servicePage__tiles__wrapper ${serv3 ? "servicePage__tiles__wrapper--scrollUp" : ""} ${serv4 ? "servicePage__tiles__wrapper--scrollUpUp" : ""}`}>
                        <div className={`servicePage__tiles__wrapper__tile ${serv1 ? "servicePage__tiles__wrapper__tile--active" : ""}`}><span>Web</span></div>
                        <div className={`servicePage__tiles__wrapper__tile ${serv2 ? "servicePage__tiles__wrapper__tile--active" : ""}`}><span>App</span></div>
                        <div className={`servicePage__tiles__wrapper__tile ${serv3 ? "servicePage__tiles__wrapper__tile--active" : ""}`}><span>Cloud</span></div>
                        <div className={`servicePage__tiles__wrapper__tile ${serv4 ? "servicePage__tiles__wrapper__tile--active" : ""}`}><span>AI</span></div>
                        <div className={`servicePage__tiles__wrapper__tile ${serv5 ? "servicePage__tiles__wrapper__tile--active" : ""}`}><span>New</span></div>
                    </div>
                </div>
                <div className="servicePage__text">
                    <FadeIn setPropActive={setServ1}>
                        <ServiceItem classNameAdded="" title={<>Do you need help with builidng your website?</>} text={<>We build can build your website using technologies such as PHP or React.</>} />
                    </FadeIn>
                    <FadeIn setPropActive={setServ2}>

                        <ServiceItem classNameAdded="" title={<>App development</>} text={<>Is a hybrid solution better then a native solution for your organisation?<br />We can help you with building an App suited for your goals.</>} />
                    </FadeIn>
                    <FadeIn setPropActive={setServ3}>
                        <ServiceItem classNameAdded="" title={<>Fascinated about the Cloud?</>} text={<>We are fascinated about the Cloud and it's possibilities. Helping organisations to make use of this potential is what we want to do.</>} />
                    </FadeIn>
                    <FadeIn setPropActive={setServ4}>
                        <ServiceItem classNameAdded="" title={<>Artificial intelligence</>} text={<>We help organisations use the full potential of all it's data by using AI. Is your organisation ready for it?</>} />
                    </FadeIn>
                    <FadeIn setPropActive={setServ5}>
                        <ServiceItem classNameAdded="" title={<>Unknown</>} text={
                            <div>
                                <div>
                                    <span>Looking for something new in the digital domain or need advise?</span>
                                </div>
                                <div className="newService__contact">
                                    <Link to="/contact">
                                        <span>Lets talk</span>
                                        <span></span>
                                    </Link>
                                </div>
                            </div>
                        } />
                    </FadeIn>
                </div>
            </div>
        </>
    );
}

export default ServicePage;
