import { noScrollBody, scrollBody } from '../../util/util.module';

interface HeaderProps {
    classNameAdded: string;
}

const Header = ({classNameAdded}: HeaderProps): JSX.Element => {

    const [showMenu, setShowMenu] = useState<boolean>(false);

    const handleClick = () => {

    }

    return (
        <div className={`header ${showMenu ? "header--active" : ""} ${classNameAdded}`}>
            <div className={`header__wrapperButton ${showMenu ? "header__wrapperButton--active" : ""}`} onClick={() => {setShowMenu(!showMenu); console.log(showMenu)}}>
                <span></span>
                <span></span>
            </div>
            <div className={`header__menu ${showMenu ? "header__menu--active" : ""}`}>
                <div className="header__menu__wrapper">
                    <Link to="/about" onClickCapture={() => setShowMenu(!showMenu) }>About</Link>
                    <Link to="/services" onClickCapture={() => setShowMenu(!showMenu) }>Services</Link>
                    <Link to="/career" onClickCapture={() => setShowMenu(!showMenu) }>Career</Link>
                    <Link to="/contact" onClickCapture={() => setShowMenu(!showMenu) }>Contact</Link>
                </div>
                <div className="header__menu__icon"><Link to="/" onClickCapture={() => setShowMenu(!showMenu) }><Icon classNameAdded="" /></Link></div>
            </div>
        </div>
    );
}

export default Header;
