import { useState, useEffect } from 'react';
import './contact.scss';
import SubmitMessage from '../submitMessage/submitMessage';
import ContactForm from '../contactForm/contactForm';
import TextBlock from '../textBlock/textBlock';

const Contact = (): JSX.Element => {

    const [submit, setSubmit] = useState<boolean>(false);
    const [submitted] = useState<boolean>(sessionStorage.getItem("submit") ? true : false);

    const title: JSX.Element = <div><div><span key="title1_1">Project?</span></div><div><span key="title1_2">Qustion?</span></div></div>;
    const text: JSX.Element = <div key="text1_1" className="contact__text"><span>Send us an e-mail or use the form.</span><a href="mailto:contact@meijer.com" className="contact__text__email">contact@meijer.com</a></div>;

    useEffect(() => {
        console.log("submit: " + submit);
        if (submit) {
            sessionStorage.setItem("submit", JSON.stringify(submit));
        }
    }, [submit]);

    return (
        <>
            <SubmitMessage classNameAdded="submitMessage__page" submit={submit} submitted={submitted}/>
            <div className={`contact ${submitted ? "contact--submitted" : ""} ${submit && !submitted ? "contact--submit" : ""}`}>
                <TextBlock title={title} text={text} />
                <ContactForm setSubmit={setSubmit} />
            </div>
        </>
    );
}

export default Contact;
