import './header.scss';
import { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import Icon from '../icon/icon';

interface HeaderProps {
    dark: boolean;
}

const Header = ({dark = false}: HeaderProps): JSX.Element => {

    const [showMenu, setShowMenu] = useState<boolean>(false);
    const [lastscroll, setLastScrolll] = useState<number>(0);
    const [showHeader, setShowHeader] = useState<boolean>(true);

    // const scrollHandler = useCallback(() => {
    //     if(lastscroll > window.scrollY || window.scrollY < 20 ){
    //         setShowHeader(true);
    //     } else {
    //         setShowHeader(false);
    //     }
    //     setLastScrolll(window.scrollY);
    // },[lastscroll]);

    // useEffect(() => {

    //     window.addEventListener('scroll', scrollHandler);
    //     return () => window.removeEventListener('scroll', scrollHandler);

    // }, [scrollHandler]);

    return (
        <div className={`header ${showHeader || showMenu ? "header--active" : ""}`}>
            <div className={`header__wrapperButton ${showMenu ? "header__wrapperButton--active" : ""} ${dark ? "header__wrapperButton--dark" : ""}`} onClickCapture={() => { setShowMenu(!showMenu); setShowHeader(true) }}>
                <span></span>
                <span></span>
            </div>
            <div className={`header__menu ${showMenu ? "header__menu--active" : ""}`}>
                <div className="header__menu__wrapper">
                    <Link to="/about" onClickCapture={() => setShowMenu(!showMenu) }>About</Link>
                    <Link to="/services" onClickCapture={() => setShowMenu(!showMenu) }>Services</Link>
                    <Link to="/career" onClickCapture={() => setShowMenu(!showMenu) }>Career</Link>
                    <Link to="/contact" onClickCapture={() => setShowMenu(!showMenu) }>Contact</Link>
                </div>
                <div className="header__menu__icon"><Link to="/" onClickCapture={() => setShowMenu(!showMenu) }><Icon classNameAdded="icon--dark" /></Link></div>
            </div>
        </div>
    );
}

export default Header;
