import React, { useState, useEffect } from 'react';
import './labelTextarea.scss';

interface LabelTextareaProps {
    classNameAdded: string;
    placeHolder: string;
}

function LabelTextarea({classNameAdded = '', placeHolder =''}: LabelTextareaProps) {

    const [textareaheight, setTextareaheight] = useState(0);

    const onChange = (event: React.ChangeEvent<HTMLTextAreaElement>): void => {

        const height = event.target.scrollHeight; 
        const heightParent: number = event.target.parentElement?.scrollHeight;

        console.log("Child: " + height);
        console.log("Parent: " + event.target.parentElement?.scrollHeight);

        if (2* height < heightParent){
            setTextareaheight(2*height);
        }
    }

    return (
        <>
            <label className={`${classNameAdded} labelTextarea`}>
                <textarea rows={textareaheight}  className="labelTextarea__input" placeholder={placeHolder} onChange={onChange} />
                <span className="labelTextarea__placeholder">{placeHolder}</span>
            </label>
        </>
    );
}

export default LabelTextarea;
