import './landing.scss';
import { useState, useEffect, useRef } from 'react';
import Header from '../header/header';
import Footer from '../footer/footer';
import PhoneRight from '../phoneRight/phoneRight';
import { Link } from 'react-router-dom';
import PcWindow from '../pcWindow/pcWindow';
import Scroll from '../scroll/scroll';
import Intro from '../intro/intro';

const Landing = (): JSX.Element => {

    const refMiddle = useRef<HTMLDivElement>(null);
    const refEnding = useRef<HTMLDivElement>(null);


    const [activeBegin, setActiveBegin] = useState<boolean>(false);
    const [activeMiddle, setActiveMiddle] = useState<boolean>(false);
    const [activeEnding, setActiveEnding] = useState<boolean>(false);
    const [intro] = useState<boolean>(!sessionStorage.getItem("intro") ? true : false);


    const messageScreen: JSX.Element = <div className={`landing__ending__phoneWrapper__messageWrapper ${activeEnding ? "landing__ending__phoneWrapper__messageWrapper--active" : ""}`}><PcWindow elements={<></>} /></div>;

    useEffect(() => {

        if(intro) {
            sessionStorage.setItem("intro", JSON.stringify(intro));
        }

        window.addEventListener('scroll', scrollHandler);

        return () => window.removeEventListener('scroll', scrollHandler);

    }, [intro]);

    const scrollHandler = () => {
        setActiveBegin(true);
        if (refMiddle && refMiddle.current && window.pageYOffset + window.innerHeight - 400 >= refMiddle.current.offsetTop) {
            setActiveMiddle(true);
        }
        if (refEnding && refEnding.current && window.pageYOffset + window.innerHeight - 400 >= refEnding.current.offsetTop) {
            setActiveEnding(true);
        }
    }

    return (
        <>
            {intro && <Intro />}
            <Header dark={false}/>
            <div className="landing">
                <div className="landing__begin" onMouseEnter={() => setActiveBegin(true)}>
                    <div className="landing__begin__wrapper">
                        <div><span>Design</span></div>
                        <div><span>Build</span></div>
                        <div><span>Maintain</span></div>
                    </div>
                </div>
                {/* <div className="landing__border"></div> */}
                <div ref={refMiddle} className={`landing__middle ${activeMiddle ? "landing__middle--active" : ""}`}>
                    <span>What can we do for you? </span>
                </div>
                {/* <div className="landing__border"></div> */}
                <div ref={refEnding} className={`landing__ending ${activeEnding ? "landing__ending--active" : ""}`}>
                    <div className="landing__ending__title">
                        <Link to="/services">
                            <div>
                                <span>Services </span>
                                <span></span>
                            </div>
                        </Link>
                    </div>
                    <div className={`landing__ending__phoneWrapper ${activeEnding ? "landing__ending__phoneWrapper--active" : ""}`}>
                        <PhoneRight classNameAdded={`phoneRight--red ${activeEnding ? "phoneRight--active" : ""}`} messageScreen={messageScreen} explode={!activeEnding} />
                    </div>
                </div>
            </div>
            <Footer />
        </>
    );
}

export default Landing;
