import './career.scss';
import Header from '../header/header';
import { useState, useEffect } from 'react';
import FadeIn from '../fadeIn/fadeIn';
import ServiceItem from '../serviceItem/serviceItem';
import { Link } from 'react-router-dom';
import Footer from '../footer/footer';

const Career = (): JSX.Element => {

    const [serv, setServ] = useState<number>(0);
    const [tile, setTile] = useState<number>(0);

    useEffect(() => {
        if (serv > tile) {
            setTile(serv);
        }
    }, [serv]);

    return (
        <>
            <Header />
            <div className="career">
            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 800 400" preserveAspectRatio="xMinYMid meet">
                        <text className={serv === 1 ? "career--active" : ""} x="10%" y="59%" fillOpacity="0">Web</text>
                        <text className={serv === 2 ? "career--active" : ""} x="10%" y="59%" fillOpacity="0">App</text>
                        <text className={serv === 3 ? "career--active" : ""} x="4%" y="59%" fillOpacity="0">Cloud</text>
                        <text className={serv === 4 ? "career--active" : ""} x="16%" y="59%" fillOpacity="0">AI</text>
                        <text className={serv === 5 ? "career--active" : ""} x="10%" y="59%" fillOpacity="0">New</text>
                    </svg>
            </div>
        </>
    );
}

export default Career;
