import './career.scss';
import Header from '../header/header';
import { useState, useEffect, useRef, useCallback } from 'react';

const Career = (): JSX.Element => {

    console.log("test");

    var vh = window.innerHeight * 0.01;
    document.documentElement.style.setProperty('--vh', vh + 'px');


    const resetHeight = useCallback(() => {
        // document.getElementById("servicePageTest")!.style.minHeight = window.innerHeight + "px";
        // document.getElementById("servicePageTest")!.style.height = window.innerHeight + "px";
        document.documentElement.style.setProperty('--vh', vh + 'px');
    },[]);

    useEffect(() => {
        // document.getElementById("servicePageTest")!.style.minHeight = window.innerHeight + "px";
        // document.getElementById("servicePageTest")!.style.height = window.innerHeight + "px";
        document.documentElement.style.setProperty('--vh', vh + 'px');

        window.addEventListener('resize', resetHeight);
        return () => window.removeEventListener('resize', resetHeight);
    }, [resetHeight]);

    return (
        <>
            <div className="career">
                <div id='test' className="career__wrapper1">
                    <div className="career__wrapper1">
                        <div className="career__wrapper1__check1">
                            <div>Begin 1</div>
                            <div>End 1</div>
                        </div>
                        <div className="career__wrapper1__check2">
                            <div>Begin 2</div>
                            <div>End 2</div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Career;
